    public class SolutionNumMatchingSubseqTests
    {
        [Fact]
        public void NumMatchingSubseq_StateUnderTest_ExpectedBehavior()
        {
            // Arrange
            var solutionNumMatchingSubseq = new SolutionNumMatchingSubseq();
            string s = "abcde";
            string[] words = ["a", "bb", "acd", "ace"];

            // Act
            var result = solutionNumMatchingSubseq.NumMatchingSubseq(
                s,
                words);

            // Assert
            result.ShouldBe(3);
        }

        [Fact]
        public void NumMatchingSubseq_StateUnderTest_ExpectedBehavior2()
        {
            // Arrange
            var solutionNumMatchingSubseq = new SolutionNumMatchingSubseq();
            string s = "dsahjpjauf";
            string[] words = ["ahjpjau","ja","ahbwzgqnuk","tnmlanowax"];

            // Act
            var result = solutionNumMatchingSubseq.NumMatchingSubseq(
                s,
                words);

            // Assert
            result.ShouldBe(2);
        }

